import axios from "axios";

axios.defaults.baseURL = "https://joshapp-backend-efcd8c73d793.herokuapp.com";
axios.defaults.withCredentials = true; // Allow sending cookies

const axiosReq = axios.create({
  headers: {
    "Content-Type": "multipart/form-data",
  },
});

const axiosRes = axios.create();

axiosRes.interceptors.response.use(
  (response) => response,
  async (error) => {
    if (error.response?.status === 401) {
      console.warn("Unauthorized - Attempting token refresh");

      try {
        const refreshResponse = await axios.post("/dj-rest-auth/token/refresh/", {}, { withCredentials: true });
        if (refreshResponse.data.access) {
          return axios(error.config); // Retry failed request with new token
        }
      } catch (refreshError) {
        console.error("Refresh token failed - Redirecting to login");
        window.location.href = "/signin"; // Redirect to login if refresh fails
      }
    }

    return Promise.reject(error);
  }
);

export { axiosReq, axiosRes };
