import axios from "axios";

axios.defaults.baseURL = "https://joshapp-backend-efcd8c73d793.herokuapp.com";
axios.defaults.withCredentials = true;

// Request instance
const axiosReq = axios.create({
  headers: {
    "Content-Type": "multipart/form-data",
  },
});

// Response instance
const axiosRes = axios.create();

axiosRes.interceptors.response.use(
  (response) => response,
  async (error) => {
    if (error.response?.status === 401) {
      console.warn("Unauthorized - Trying to refresh token");

      try {
        const refreshResponse = await axios.post(
          "/dj-rest-auth/token/refresh/",
          {}, 
          { withCredentials: true }
        );

        if (refreshResponse.data.access) {
          axios.defaults.headers.common[
            "Authorization"
          ] = `Bearer ${refreshResponse.data.access}`;
          return axios(error.config);
        }
      } catch (refreshError) {
        console.error("Token refresh failed - Logging out");
        window.location.href = "/signin";
      }
    }

    return Promise.reject(error);
  }
);

export { axiosReq, axiosRes };
